function SetLike(n){n=="like"?LikeYes():n=="no like"?LikeNo():n=="none"&&LikeNone()}function LikeYes(){$("#like > img").attr("src","/icons/like yes.png");$("#like > p").css("color","#FF0000")}function LikeNo(){$("#like > img").attr("src","/icons/like no.png");$("#like > p").css("color","#747A8D")}function LikeNone(){$("#like").css("cursor","default");$("#like > img").attr("src","/icons/like none.png");$("#like > p").css("color","#666666")}