function SetLike(n,t){n=="like"?LikeYes(t):n=="no like"?LikeNo(t):n=="none"&&LikeNone(t)}function LikeYes(n){$(n+" > img").attr("src","/icons/like yes.png");$(n+" > p").css("color","#FF0000")}function LikeNo(n){$(n+" > img").attr("src","/icons/like no.png");$(n+" > p").css("color","#747A8D")}function LikeNone(n){$(n).css("cursor","default");$(n+" > img").attr("src","/icons/like none.png");$(n+" > p").css("color","#666666")}